                          -(Lc3*m3*sin(qr3)*(dq0 - dqr2)*(L2*m0 + L2*m1 + L2*m2 - Lc2*m2))/(m0 + m1 + m2 + m3),                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               0];
 

  
